mapper.update:
update student
set student_id=#{studentId}, phonenum=#{phonenum}
where stu_no=#{stuNo}

update student
set major=#{major}, stu_no=#{stuNo}, student_id=#{studentId}, name=#{name}, 
	pwd=#{pwd}, semester=#{semester}, admission_date=#{admissionDate},
	graduation_date=#{graduationDate}, credits=#{credits}, state=#{state},
	phonenum=#{phoneNum}
where stu_no = #{stuNo}
